let contactsButton=document.querySelector(".contacts-text .button"),popup=document.querySelector(".modal"),focusImput=popup.querySelector("[name=name]"),buttonClose=popup.querySelector(".button-close"),loginForm=popup.querySelector(".email-form"),loginName=popup.querySelector(".login-name"),loginEmail=popup.querySelector(".login-email"),loginText=popup.querySelector(".login-text");contactsButton.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),focusImput.focus()}),buttonClose.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error")}),window.addEventListener("keydown",function(e){"Esc"!==e.key&&"Escape"!==e.key||popup.classList.contains("modal-show")&&(e.preventDefault(),popup.classList.remove("modal-show"),popup.classList.remove("modal-error"))}),loginForm.addEventListener("submit",function(e){loginName.value&&loginEmail.value&&loginText.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.offsetWidth=popup.offsetWidth,popup.classList.add("modal-error"))});